    type: types.FOLLOWERS_FETCH_STARTED,
    });

export const completeFolllwersFetch = (entities, order, userid) => ({
    type: types.FOLLOWERS_FETCH_COMPLETED,
    payload: {
        entities,
        order,
        userid,
    },
    });

