        // yield put(actions.failLogin(non_field_errors[0]));
      }
    }
  } catch (error) {
    // yield put(actions.failLogin(''));
  }
 }


  function* addTopic(action) {
    try {
      const isAuth = yield select(selectors.isAuthenticated);

      if (isAuth) {
        const token = yield select(selectors.getAuthToken);
        const response = yield call(
          fetch,
          `${API_BASE_URL}/topic/`,
          {
            method: 'POST',
            body: JSON.stringify({...action.payload.content}),
            headers:{
              'Content-Type': 'application/json',
              'Authorization': `JWT ${token}`,
            },
          }
        );

        if (response.status === 201) {
          const topic = yield response.json();
          action.payload.content.hashtags.split('#').map(content=>{
            addHashtag(content,topic.id)
          })
          yield put(
            actions.completeAddingTopic(
              action.payload.id,
              topic,
            ),
          );
