          // const { non_field_errors } = yield response.json();
          // yield put(actions.failLogin(non_field_errors[0]));
        }
      }
    } catch (error) {
      // yield put(actions.failLogin(''));
    }
  }

  export function* watcTopicAddition() {
    yield takeEvery(
      types.TOPIC_ADDITION_STARTED,
      addTopic,
    );
  }
