import React, { useState, useEffect, useRef } from 'react';
import { Text, View, TouchableOpacity } from 'react-native';
import { Camera } from 'expo-camera'; 
import styles from  './styles'
import { normalize } from '../../utils/normalize';
import {colors} from '../../../configuration'
import TokenRefresh from '../TokenRefresh';


const MyCamera = () => {
  const [hasPermission, setHasPermission] = useState(null);
  const [cameraRef, setCameraRef] = useState(null)
  const [type, setType] = useState(Camera.Constants.Type.back);useEffect(() => {
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }, []);if (hasPermission === null) {
    return <View />;
  }
  if (hasPermission === false) {
    return <Text>No access to camera</Text>;
  }
  return (
    <View style={styles.mainContainer}>
        <Camera style={styles.camera} type={type} ref={ref => {
          setCameraRef(ref) ;
        }}>
          <View
            style={styles.container}>
            <TouchableOpacity
              style={styles.iconContainer}
              onPress={() => {
                setType(
                  type === Camera.Constants.Type.back
                    ? Camera.Constants.Type.front
                    : Camera.Constants.Type.back
                );
              }}>
              <Text style={{ fontSize: 18, marginBottom: 10, color: 'white' }}> Flip </Text>
            </TouchableOpacity>
            <TouchableOpacity style={{alignSelf: 'center', marginBottom: normalize(15)}} onPress={async() => {
              if(cameraRef){
                let photo = await cameraRef.takePictureAsync();
                console.log('photo', photo);
              }
            }}>
              <View style={styles.pictureButton1}
              >
                <View style={styles.pictureButton2} >
                </View>
              </View>
            </TouchableOpacity>
          </View>
        </Camera>
      <TokenRefresh/>
    </View>
  );
}

export default MyCamera
