import { StyleSheet } from 'react-native';
import { colors } from '../../../configuration';
import { normalize } from '../../utils/normalize'

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: colors.primary,
      alignItems: 'center',
      justifyContent: 'center',
      borderColor: colors.primary,
    },
    error: {
        color : colors.secondary,
        fontSize: 9,
        margin: normalize(5)
    },
    text: {
        marginTop: normalize(5),
        color : colors.white,
        fontSize: 9,
    },
    link: {
        marginTop: normalize(5),
        color : colors.white,
        fontSize: 9,
    },
    input: {
        width: normalize(3000),
        maxWidth: 800,
        height: normalize(30),
        backgroundColor: colors.white,
        borderRadius: 7,
        fontSize: normalize(5),
        margin: normalize(1.5),
        padding: 10,
        borderColor: colors.white,
        borderWidth: 1
    },
    button:{
        borderRadius: 20,
        width: normalize(200),
        justifyContent: 'center',
        alignItems: 'center',
        alignSelf: 'center',
        marginTop: normalize(20)
    },
    logo: {
        height: normalize(680),
        width: normalize(350),
        margin: normalize(50),
      },
    buttons:{
      alignItems: 'center',
      justifyContent: 'center',
    },
    errors:{
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: normalize(15)
    },
    option:{
        flexDirection: 'row',
        alignItems: 'baseline',
        justifyContent: 'center',
    }
  });

export default styles
