export const gridList = array => {
    let response = []
    let row = []
    for (let i = 0; i < array.length; i++) {
        row = [...row,array[i]]
        if(i%3==2){
            response = [...response,row]
            row = []
        }
        console.log(response)
    }
    return response
}
