import { Camera } from 'expo';
import {Text, View, Image, TouchableOpacity, ScrollView, TextInput,StyleSheet } from 'react-native';
import React, {useEffect, useState} from 'react';


const MyCamera = () => (
    <View style={cam_styles.container}>
        <Camera ref={cam=>{this.camera = cam;}} style={cam_styles.preview} aspect = {Camera.constants.Aspect.fill}>
                <Text style={cam_styles.capture} onPress={this.takePicture.bind(this)}>[CAPTURE]</Text>
            </Camera>
    </View>
);

const cam_styles = StyleSheet.create({
    container: {
        flex:1,
        flexDirection: 'row',
    },
    preview:{
        flex:1,
        justifyContent: 'flex-end',
        alignItems:'center'
    },
    capture:{
        flex:0,
        backgroundColor: '#fff',
        borderRadius: 5,
        color:'#000',
        padding:10,
        margin:40
    }
});

export default MyCamera
