import { connect } from 'react-redux';
import { Text, View, TextInput, Button, Image } from 'react-native';
import * as actions from '../../actions/auth';
import * as selectors from '../../reducers';
import React, { useState } from 'react';
import { reduxForm, Field } from 'redux-form'
import styles from './styles'
import { colors } from '../../../configuration';
import { Link } from "react-router-dom";
import { Actions } from 'react-native-router-flux';
import { normalize } from '../../utils/normalize';
import { LinearGradient } from 'expo-linear-gradient';
import { URL } from '../../../configuration'
import Spinner from 'react-native-loading-spinner-overlay';
import { Form } from 'native-base';

const Login = ({
  onSubmit,
  isLoading,
  error = null,
}) => {


  const validate = values => {
    const error= {};
    error.email= '';
    error.name= '';
    var ema = values.email;
    var nm = values.name;
    if(values.email === undefined){
      ema = '';
    }
    if(values.name === undefined){
      nm = '';
    }
    if(ema.length < 8 && ema !== ''){
      error.email= 'too short';
    }
    if(!ema.includes('@') && ema !== ''){
      error.email= '@ not included';
    }
    if(nm.length > 8){
      error.name= 'max 8 characters';
    }
    return error;
  };


  const [username, changeUsername] = useState('');
  const [password, changePassword] = useState('');

  const renderInput = ({ input, label, type, placeholder, security, meta: { touched, error, warning } })=>{
    var hasError= false;
    if(error !== undefined){
      hasError= true;
    }
    return( 
      <View error= {hasError}>
        <TextInput
        style={styles.input}
        placeholder={placeholder}
        secureTextEntry={security}
      />
        {hasError ? <Text>{error}</Text> : <Text />}
      </View>
    )
  }

  return (
    <View style={styles.container}>
      <Form>
      <LinearGradient
          colors={[colors.primary, 'transparent']}
          style={{
            width: '100%',
            height: '100%',
            alignItems: 'center',
            justifyContent: 'center'
          }}
        >
      <View style={styles.centered}>
      <Image style={styles.logo} source={require('../../public/static/img/logo.png')} ></Image>
      <Field name="email" component={renderInput} placeholder={'username'} security={false} />
      <Field name="email" component={renderInput} placeholder={'password'} security={true} />
      {/* <TextInput
        style={styles.input}
        className="user"
        type="text"
        placeholder="username"
        value={username}   
        onChangeText={changeUsername}
        onChange={e => changeUsername(e.target.value)}
      />
      <TextInput
          style={styles.input}
          className="password"
          type="password"
          secureTextEntry={true}
          placeholder="password"
          value={password}
          onChangeText={changePassword}
          onChange={e=>changePassword(e.target.value)}
      /> */}
      <View style={styles.option}>
        <Text style={styles.text} >{"Did you forget your password? "}</Text>
        {
          (typeof document === 'undefined')?(
            <Text style={styles.link} title={' reset password '}
            onPress={() =>
              Actions.ResetPassword(true)
            }>{' reset password '}</Text>
          ):(
            <Link to="/reset-password" style={styles.navItem}>
              <Text style={styles.text}>{' reset password '}</Text>
            </Link>
          )
        }

      </View>
      <View style={styles.errors}>
        {
        error && (
            <Text style={styles.error}>{ error }</Text>
        )
      }
        {
          isLoading ? (
            <View>
              <Text style={styles.error}>{'LOADING...'}</Text>
            </View>
          ) : (
            <View> 
              <View style={styles.button}>
                  <Text style={styles.button} type="submit" onPress={
                      onSubmit
                  }>{'LOGIN'}</Text>
              </View>
              <View style={styles.option}>
                <Text style={styles.text} >{"Do you have an account?  "}</Text>
                {
                  (typeof document === 'undefined')?(
                    <Text style={styles.link} title={' register now '}
                    onPress={() =>
                      Actions.SignUp(true)
                    }>{' register now '}</Text>
                  ):(
                    <Link to="/signup" style={styles.navItem}>
                      <Text style={styles.text}>{' register now'}</Text>
                    </Link>
                  )
                }

              </View>
            </View>
          )
        }
      </View>
      </View>
      </LinearGradient>
      </Form>
    </View>
  );
} 

const myLogin = connect(
  state => ({
    isLoading: selectors.getIsAuthenticating(state),
    error: selectors.getAuthenticatingError(state),
    isAuthenticated: selectors.isAuthenticated(state),
  }),
  dispatch => ({
    onSubmit(username) {
      console.log(username)
      if(username && password){
        dispatch(actions.startLogin(username, password))
      }
      else if(!username){
        dispatch(actions.failLogin('WRITE A VALID USERNAME'))
      }
      else if(!password){
        dispatch(actions.failLogin('WRITE A VALID PASSWORD'))
      }
    },
  }),
  (stateToProps,disptachToProps) => {
    if(stateToProps.isAuthenticated){
      if(typeof document !== 'undefined'){
        window.location.href = URL
      }
      else{
        Actions.replace('Home')
      }
    }
    return ({...disptachToProps,...stateToProps})
  }
)(Login);

export default reduxForm({
  form: 'login',
})(myLogin)
