import { Dimensions, Platform, PixelRatio } from 'react-native';

const {
  width: SCREEN_WIDTH,
  height: SCREEN_HEIGHT,
} = Dimensions.get('window');

// based on iphone 5s's scale
const scale = SCREEN_WIDTH / 320;

export function normalize(size) {
  const newSize = size * scale 
  if (Platform.OS === 'ios') {
    return Math.round(PixelRatio.roundToNearestPixel(-Math.log(newSize/2)+Math.pow(newSize/2,(1/2))))*10
  } else {
    return Math.round(PixelRatio.roundToNearestPixel(-Math.log(newSize/2)+Math.pow(newSize/2,(1/2))))*10 - 2
  }
}
